package io.jsd.training.designpattern.book.chap3.Meteo.meteo;public class WidgetMeteo implements Observateur {    protected DonneesMeteo donneesMeteo;    protected Double temperature;    protected Double vitesseVent;    protected Double tauxHumidite;    public WidgetMeteo(DonneesMeteo donneesMeteo) {        this.donneesMeteo = donneesMeteo;        donneesMeteo.ajoute(this);        actualise();    }    public void actualise() {        temperature = donneesMeteo.getTemperature();        vitesseVent = donneesMeteo.getVitesseVent();        tauxHumidite = donneesMeteo.getTauxHumidite();        affiche();    }    public void affiche() {        System.out                .println("Le Widget affiche une température de: "                        + temperature                        + ", une vitesse de vent de: "                        + vitesseVent                        + " et un taux d’humidité de:"                        + tauxHumidite);    }}