package io.jsd.training.java.designpattern.chapitres.chap3.ObjetsGraphiques.objetsgraphiques;public class CommandeFlecheBasZT implements Commande {    protected ZoneTexte recepteur;    public CommandeFlecheBasZT(ZoneTexte r) {        this.recepteur = r;    }    public void lance() {        int taille = recepteur.getTaillePolice();        if (taille > 0)            recepteur.setTaillePolice(taille - 1);    }}